import{detailFilms}from"../js/api";const movieDetail=document.querySelector("#movieDetail"),urlParams=new URLSearchParams(window.location.search),movieId=urlParams.get("id");(async()=>{const e=await detailFilms(movieId),t=`\n        <img src="https://image.tmdb.org/t/p/w500/${e.poster_path}" alt="${e.title}">\n        <div class="detail-container">\n            <h2 class="txt-detail">${e.title}</h2>\n            <p class="txt-detail">${e.overview}</p>\n            <ul>\n                <li class="txt-detail">Release Date: ${e.release_date}</li>\n                <li class="txt-detail">Vote Average: ${e.vote_average}</li>\n                <li class="txt-detail">Runtime: ${e.runtime} minutes</li>\n                <li class="txt-detail">Genres: ${e.genres.map((e=>e.name)).join(", ")}</li>\n            </ul>\n        </div>\n    `;movieDetail.innerHTML=t})();