import{newMovies}from"../js/api";import style from"../js/style";class newMovie extends HTMLElement{constructor(){super(),this.attachShadow({mode:"open"});const e=document.createElement("template");e.innerHTML=`\n        ${style}\n        <div class="movieContiner">\n            <div class="movieItem">\n    \n            </div>\n        </div>\n        `,this.shadowRoot.appendChild(e.content.cloneNode(!0))}async connectedCallback(){const e=await newMovies(),t=this.shadowRoot.querySelector(".movieItem");e.slice(0,5).forEach((e=>{const n=document.createElement("div");n.classList.add("movie"),n.innerHTML=`\n            <img src="https://image.tmdb.org/t/p/w500/${e.poster_path}" alt="${e.title}">\n            <h3>${e.title}</h3>\n            <p>${e.release_date}</p>\n            <button class="btn-detail" data-movie-id="${e.id}">Detail</button>\n            `,t.appendChild(n)})),this.shadowRoot.querySelectorAll(".btn-detail").forEach((e=>{e.addEventListener("click",(()=>{const t=e.getAttribute("data-movie-id");window.location.href=`page/film.html?id=${t}`}))}))}}customElements.define("new-films",newMovie);