import{detailTveseries}from"../js/api";const tvDetail=document.querySelector("#movieDetail"),urlParams=new URLSearchParams(window.location.search),tvId=urlParams.get("id");(async()=>{const e=await detailTveseries(tvId),t=`\n        <img src="https://image.tmdb.org/t/p/w500/${e.poster_path}" alt="${e.name}">\n        <div class="detail-container">\n            <h2 class="txt-detail">${e.name}</h2>\n            <p class="txt-detail">${e.overview}</p>\n            <ul>\n                <li class="txt-detail">First Air Date: ${e.first_air_date}</li>\n                <li class="txt-detail">Vote Average: ${e.vote_average}</li>\n                <li class="txt-detail">Number of Episodes: ${e.number_of_episodes}</li>\n                <li class="txt-detail">Number of Seasons: ${e.number_of_seasons}</li>\n                <li class="txt-detail">Genres: ${e.genres.map((e=>e.name)).join(", ")}</li>\n            </ul>\n        </div>\n    `;tvDetail.innerHTML=t})();