import axios from"axios";const API_KEY="d512e52ea3248a19b019f36b716c3bd1",BASE_URL="https://api.themoviedb.org/3",getRandomMovies=e=>{const a=[];for(;a.length<10;){const t=e[Math.floor(Math.random()*e.length)];a.includes(t)||a.push(t)}return a};export const newMovies=async()=>{const e=`${BASE_URL}/movie/now_playing?api_key=${API_KEY}&language=en-US&page=1`,a=await axios.get(e);return getRandomMovies(a.data.results)};export const popularMovies=async()=>{const e=`${BASE_URL}/movie/popular?api_key=${API_KEY}&language=en-US&page=1`,a=await axios.get(e);return getRandomMovies(a.data.results)};export const superHeroMovies=async()=>{const e=`${BASE_URL}/discover/movie?api_key=${API_KEY}&with_genres=878&language=id-US&page=1`,a=await axios.get(e);return getRandomMovies(a.data.results)};export const indonesianFilms=async()=>{const e=`${BASE_URL}/discover/movie?api_key=${API_KEY}&with_original_language=id&language=id-US&page=1`,a=await axios.get(e);return getRandomMovies(a.data.results)};export const detailFilms=async e=>{const a=`${BASE_URL}/movie/${e}?api_key=${API_KEY}&language=id-US`;return(await axios.get(a)).data};export const detailTveseries=async e=>{const a=`${BASE_URL}/tv/${e}?api_key=${API_KEY}&language=id-US`;return(await axios.get(a)).data};export const tvSeries=async()=>{const e=`${BASE_URL}/discover/tv?api_key=${API_KEY}&with_networks=213`;return(await axios.get(e)).data.results};