const style="\n<style>\n    .movieContiner{\n        width: 100%;\n    }\n    .movieItem{\n        display: grid;\n        grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));\n        gap: 2rem;\n        margin: 1rem 0;\n    }\n    \n    .movie {\n        width: 200px;\n        padding: 1rem;\n        border-radius:5px;\n        background: #1a1a1a;\n        box-shadow: rgba(0, 0, 0, 0.4) 0px 1px 2px, rgba(0, 0, 0, 0.3) 0px 3px 6px -3px, rgba(0, 0, 0, 0.2) 0px -2px 0px inset;\n    }\n    \n    .movie > img{\n        width: 12.4rem;\n        border: 1px solid black;\n    }\n    \n    .movie > h3{\n        color: #fff;\n        text-align: center;\n        font-size: 18px;\n        overflow: hidden;\n        text-overflow: ellipsis;\n        white-space: nowrap;\n    }\n\n    .movie > p{\n        color: #fff;\n        font-size: 12px;\n        text-align: center;\n    }\n    \n    .btn-detail{\n        border:none;\n        background-color: #2c2c2c;\n        padding: 10px 15px;\n        width: 100%;\n        color: #6896c7;\n        font-size:14px;\n\n        border-radius:2px;\n    }\n    .btn-detail:hover{\n        background-color: rgba(87, 153, 239, 0.133);\n    }\n</style>\n";export default style;